#include <map>
#include <vector>
#include <string>
#include <stdio.h>
#include <iostream>

using namespace std;
map<char,int> varMap;

multimap<int,string> convertStrToMap(string operation)
{
    multimap<int,string> mapOp;
    size_t len = operation.length();
    int rank = 0;
    int prior = 0;
    for (int i = 0; i < len; i++) {
        char letter = operation[i];
        if (letter == '<') {
            string str = operation.substr(prior, i - prior);
            mapOp.insert(make_pair(rank, str));
            if (i - prior == 1) {
                if (str[0] >= 'A' && str[0] <= 'Z') {
                    varMap.insert(make_pair(str[0], 0));
                }
            }
            prior = i + 1;
            rank++;
        }
        if (letter == '=') {
            prior = i + 1;
            rank--;
        }
    }
    string str = operation.substr(prior, len - prior);
    mapOp.insert(make_pair(rank, str));
    if (len - prior == 1) {
        if (str[0] >= 'A' && str[0] <= 'Z') {
            varMap.insert(make_pair(str[0], 0));
        }
    }
    return mapOp;
}

int findValue(string input)
{

    if (input.size() == 1 && input[0] >= 'A' && input[0] <= 'Z') {
        return varMap[input[0]];
    }
    return atoi(input.c_str());
}

void checker(vector<multimap<int,string>> opVec)
{
    bool pass = true;
    for (int i = 0; i < opVec.size(); i++) {
        multimap<int,string> operation = opVec[i];
        multimap<int,string>::iterator it;
        int rank1 = -1, rank2 = -1;
        int value1, value2;
        string var1, var2;
        for (it = operation.begin(); it !=operation.end(); it++) {
            if (rank1 == -1) {
                rank1 = it->first;
                var1 = it->second;
                continue;
            }
            rank2 = it->first;
            var2 = it->second;
            value1 = findValue(var1);
            value2 = findValue(var2);
            if (rank1 == rank2) { // <=
                if (value1 <= value2) {
                    pass = true;
                }
                else
                {
                    pass = false;
                }
            }
            else // <
            {
                if (value1 < value2) {
                    pass = true;
                }
                else
                {
                    pass = false;
                }
            }

            if (!pass) {
                break;
            }

            var1 = var2;
            rank1 = rank2;
        }

        if (!pass) {
            break;
        }
    }

    if (pass) {
        cout<<"Yes"<<endl;
    }
    else
    {
        cout<<"No"<<endl;
    }
}

int main()
{
    int N;
    scanf("%d",&N);
    vector<multimap<int,string>> opVec;
    while (N--) {
        string op;
        cin >> op;
        multimap<int,string> operation = convertStrToMap(op);
        opVec.push_back(operation);
    }
    int T;
    scanf("%d",&T);
    while (T--) {
        size_t numOfVar = varMap.size();
        while (numOfVar--) {
            char letter;
            int value;
            cin>>letter>>value;
            varMap[letter] = value;
        }
        checker(opVec);
    }
    return 0;
}

