    printf("copyArray pointer address: %p\n",&arr2);
    printf("\n");
    
    NSMutableArray *arr3 = [arr1 mutableCopy];
    [arr3 addObject:@"World"];
    [[arr1 objectAtIndex:0] appendString:@" + str1"];
